package Operators;

public class factorial {

	public static void main(String[] args) {
		int number =5;
		long factorial =1;
		for (int i=1; i<= number;i++) {
			factorial*=1;
		}
		
		System.out.println("factorial of"+ "number+is:"+factorial);
		// TODO Auto-generated method stub

	}

}
